mapper.select:
select a.* from tb_user a left join tb_user_info b on a.id_card=b.id_card


        a.user_name like concat('%',#{userName},'%')


        and a.birthday >= #{beginAge}


        and a.birthday <= #{endAge}


        and b.info_code in

            #{item}



limit #{startIndex},#{pageSize}

select count(*) from tb_user a left join tb_user_info b on a.id_card=b.id_card


        a.user_name like concat('%',#{userName},'%')


        and a.birthday >= #{beginAge}


        and a.birthday <= #{endAge}


        and b.info_code in

            #{item}

select * from tb_user where version=#{version}
    union
select * from tb_user where user_name like concat('%',#{userName},'%')

select * from tb_user where version=#{version}
    union all
select * from tb_user where user_name like concat('%',#{userName},'%')
